import React, { useState, useEffect } from 'react';

const DashboardAdministrador = ({ user }) => {
    // Estados para los modales y formularios
    const [showAgregarCurso, setShowAgregarCurso] = useState(false);
    const [showGestionUsuarios, setShowGestionUsuarios] = useState(false);
    const [showGestionCalificaciones, setShowGestionCalificaciones] = useState(false);
    const [showAsignarDocente, setShowAsignarDocente] = useState(false);
    const [cursoSeleccionado, setCursoSeleccionado] = useState(null);
    const [nuevoCurso, setNuevoCurso] = useState({ nombre: '', descripcion: '', docente: '' });
    const [calificacionEdit, setCalificacionEdit] = useState({ id: null, nota: '', estudiante: '', curso: '' });
    
    // Estados para datos de la base de datos
    const [estadisticas, setEstadisticas] = useState(null);
    const [usuarios, setUsuarios] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Función para cargar estadísticas desde la base de datos
    const cargarEstadisticas = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/admin/estadisticas');
            if (response.ok) {
                const data = await response.json();
                setEstadisticas(data.data);
            } else {
                console.error('Error al cargar estadísticas');
                setError('Error al cargar estadísticas');
            }
        } catch (error) {
            console.error('Error de conexión:', error);
            setError('Error de conexión con el servidor');
        }
    };

    // Función para cargar usuarios desde la base de datos
    const cargarUsuarios = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/admin/usuarios');
            if (response.ok) {
                const data = await response.json();
                setUsuarios(data.data);
            } else {
                console.error('Error al cargar usuarios');
                setError('Error al cargar usuarios');
            }
        } catch (error) {
            console.error('Error de conexión:', error);
            setError('Error de conexión con el servidor');
        }
    };

    // Cargar datos al montar el componente
    useEffect(() => {
        const cargarDatos = async () => {
            setLoading(true);
            await Promise.all([cargarEstadisticas(), cargarUsuarios()]);
            setLoading(false);
        };
        cargarDatos();
    }, []);

    // Filtrar solo docentes
    const docentesDisponibles = usuarios.filter(usuario => usuario.rol === 'Docente' && usuario.activo);

    const cursosActivos = [
        { id: 1, nombre: 'Matemáticas Avanzadas', docente: 'Prof. Juan Pérez', estudiantes: 25, estado: 'Activo' },
        { id: 2, nombre: 'Literatura Universal', docente: 'Prof. María González', estudiantes: 18, estado: 'Activo' },
        { id: 3, nombre: 'Ciencias Naturales', docente: 'Prof. Carlos Rodríguez', estudiantes: 22, estado: 'Activo' },
        { id: 4, nombre: 'Historia del Arte', docente: 'Prof. Ana López', estudiantes: 15, estado: 'Inactivo' }
    ];

    const calificacionesRecientes = [
        { id: 1, estudiante: 'Ana García', curso: 'Matemáticas Avanzadas', tarea: 'Examen Parcial', nota: 85, fecha: '2024-01-10' },
        { id: 2, estudiante: 'Carlos López', curso: 'Literatura Universal', tarea: 'Ensayo de Poesía', nota: 92, fecha: '2024-01-09' },
        { id: 3, estudiante: 'María Rodríguez', curso: 'Ciencias Naturales', tarea: 'Proyecto de Laboratorio', nota: 78, fecha: '2024-01-08' },
        { id: 4, estudiante: 'Pedro Sánchez', curso: 'Historia del Arte', tarea: 'Análisis de Obra', nota: 88, fecha: '2024-01-07' }
    ];

    const alertasSistema = [
        { id: 1, tipo: 'Sistema', mensaje: 'Backup automático completado exitosamente', fecha: '2024-01-12', prioridad: 'baja' },
        { id: 2, tipo: 'Usuario', mensaje: '3 nuevos usuarios registrados hoy', fecha: '2024-01-12', prioridad: 'media' },
        { id: 3, tipo: 'Curso', mensaje: 'Curso "Historia del Arte" necesita asignación de docente', fecha: '2024-01-11', prioridad: 'alta' }
    ];

    // Funciones para manejar formularios
    const handleAgregarCurso = (e) => {
        e.preventDefault();
        // Aquí iría la lógica para agregar el curso
        console.log('Agregando curso:', nuevoCurso);
        setNuevoCurso({ nombre: '', descripcion: '', docente: '' });
        setShowAgregarCurso(false);
        alert('Curso agregado exitosamente');
    };

    const handleAsignarDocente = (curso) => {
        setCursoSeleccionado(curso);
        setShowAsignarDocente(true);
    };

    const handleConfirmarAsignacion = (docenteId) => {
        const docente = docentesDisponibles.find(d => d.id === docenteId);
        if (docente && cursoSeleccionado) {
            console.log(`Asignando ${docente.nombre} ${docente.apellido} al curso ${cursoSeleccionado.nombre}`);
            alert(`Docente ${docente.nombre} ${docente.apellido} asignado al curso ${cursoSeleccionado.nombre}`);
            setShowAsignarDocente(false);
            setCursoSeleccionado(null);
        }
    };

    const handleCambiarRol = (usuarioId, nuevoRol) => {
        // Aquí iría la lógica para cambiar el rol
        console.log('Cambiando rol del usuario:', usuarioId, 'a:', nuevoRol);
        alert(`Rol cambiado a ${nuevoRol} exitosamente`);
    };

    const handleEditarCalificacion = (calificacion) => {
        setCalificacionEdit(calificacion);
        setShowGestionCalificaciones(true);
    };

    const handleGuardarCalificacion = (e) => {
        e.preventDefault();
        // Aquí iría la lógica para guardar la calificación
        console.log('Guardando calificación:', calificacionEdit);
        setShowGestionCalificaciones(false);
        alert('Calificación actualizada exitosamente');
    };

    return (
        <div className="dashboard-container">
            {/* Header del Dashboard */}
            <div className="dashboard-header">
                <div>
                    <h1 className="dashboard-title">Dashboard Administrador</h1>
                    <p className="dashboard-subtitle">Panel de control administrativo</p>
                </div>
                <div className="user-info">
                    <p className="user-name">{user.nombre} {user.apellido}</p>
                    <p className="user-role">👑 Administrador</p>
                </div>
            </div>

            {/* Estadísticas Generales */}
            <div className="dashboard-grid">
                <div className="dashboard-card">
                    <div className="card-header">
                        <div className="card-icon">👥</div>
                        <h3 className="card-title">Total Usuarios</h3>
                    </div>
                    <div className="card-content">
                        <p><strong>{estadisticasGenerales.totalUsuarios}</strong> usuarios registrados</p>
                        <p>Estudiantes: {estadisticasGenerales.totalEstudiantes}</p>
                        <p>Docentes: {estadisticasGenerales.totalDocentes}</p>
                        <p>Padres: {estadisticasGenerales.totalPadres}</p>
                    </div>
                </div>

                <div className="dashboard-card">
                    <div className="card-header">
                        <div className="card-icon">📚</div>
                        <h3 className="card-title">Cursos Activos</h3>
                    </div>
                    <div className="card-content">
                        <p><strong>{estadisticasGenerales.totalCursos}</strong> cursos en el sistema</p>
                        <p>Activos: {cursosActivos.filter(c => c.estado === 'Activo').length}</p>
                        <p>Inactivos: {cursosActivos.filter(c => c.estado === 'Inactivo').length}</p>
                    </div>
                </div>

                <div className="dashboard-card">
                    <div className="card-header">
                        <div className="card-icon">📊</div>
                        <h3 className="card-title">Estadísticas</h3>
                    </div>
                    <div className="card-content">
                        <p>Promedio general: <strong>85.2%</strong></p>
                        <p>Asistencia promedio: <strong>92%</strong></p>
                        <p>Usuarios activos: <strong>94%</strong></p>
                    </div>
                </div>
            </div>

            {/* Gestión de Cursos */}
            <div className="dashboard-card">
                <div className="card-header">
                    <div className="card-icon">📚</div>
                    <h3 className="card-title">Gestión de Cursos</h3>
                    <button 
                        className="btn-primary"
                        onClick={() => setShowAgregarCurso(true)}
                    >
                        + Agregar Curso
                    </button>
                </div>
                <div className="card-content">
                    <div className="cursos-list">
                        {cursosActivos.map(curso => (
                            <div key={curso.id} className="curso-item">
                                <div className="curso-info">
                                    <h4>{curso.nombre}</h4>
                                    <p>Docente: {curso.docente}</p>
                                    <p>Estudiantes: {curso.estudiantes}</p>
                                </div>
                                <div className="curso-status">
                                    <span className={`status-badge ${curso.estado.toLowerCase()}`}>
                                        {curso.estado}
                                    </span>
                                </div>
                                <div className="curso-actions">
                                    <button className="btn-secondary">Editar</button>
                                    <button 
                                        className="btn-primary"
                                        onClick={() => handleAsignarDocente(curso)}
                                    >
                                        Asignar Docente
                                    </button>
                                    <button className="btn-danger">Eliminar</button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Gestión de Usuarios */}
            <div className="dashboard-card">
                <div className="card-header">
                    <div className="card-icon">👥</div>
                    <h3 className="card-title">Gestión de Usuarios</h3>
                    <button 
                        className="btn-primary"
                        onClick={() => setShowGestionUsuarios(true)}
                    >
                        Ver Todos
                    </button>
                </div>
                <div className="card-content">
                    <div className="usuarios-list">
                        {usuariosRecientes.map(usuario => (
                            <div key={usuario.id} className="usuario-item">
                                <div className="usuario-info">
                                    <h4>{usuario.nombre} {usuario.apellido}</h4>
                                    <p>{usuario.correo}</p>
                                    <p>Registrado: {usuario.fechaRegistro}</p>
                                </div>
                                <div className="usuario-rol">
                                    <select 
                                        value={usuario.rol}
                                        onChange={(e) => handleCambiarRol(usuario.id, e.target.value)}
                                        className="rol-select"
                                    >
                                        <option value="Estudiante">Estudiante</option>
                                        <option value="Docente">Docente</option>
                                        <option value="Padre">Padre</option>
                                        <option value="Administrador">Administrador</option>
                                    </select>
                                </div>
                                <div className="usuario-status">
                                    <span className={`status-badge ${usuario.activo ? 'activo' : 'inactivo'}`}>
                                        {usuario.activo ? 'Activo' : 'Inactivo'}
                                    </span>
                                </div>
                                <div className="usuario-actions">
                                    <button className="btn-secondary">Editar</button>
                                    <button className="btn-danger">Eliminar</button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Gestión de Calificaciones */}
            <div className="dashboard-card">
                <div className="card-header">
                    <div className="card-icon">📝</div>
                    <h3 className="card-title">Gestión de Calificaciones</h3>
                </div>
                <div className="card-content">
                    <div className="calificaciones-list">
                        {calificacionesRecientes.map(cal => (
                            <div key={cal.id} className="calificacion-item">
                                <div className="cal-info">
                                    <h4>{cal.estudiante}</h4>
                                    <p>{cal.curso} - {cal.tarea}</p>
                                    <p>Fecha: {cal.fecha}</p>
                                </div>
                                <div className="cal-nota">
                                    <input 
                                        type="number" 
                                        value={cal.nota}
                                        onChange={(e) => {
                                            const updatedCal = { ...cal, nota: e.target.value };
                                            setCalificacionEdit(updatedCal);
                                        }}
                                        className="nota-input"
                                        min="0"
                                        max="100"
                                    />
                                </div>
                                <div className="cal-actions">
                                    <button 
                                        className="btn-primary"
                                        onClick={() => handleEditarCalificacion(cal)}
                                    >
                                        Guardar
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Alertas del Sistema */}
            <div className="dashboard-card">
                <div className="card-header">
                    <div className="card-icon">🚨</div>
                    <h3 className="card-title">Alertas del Sistema</h3>
                </div>
                <div className="card-content">
                    <div className="alertas-list">
                        {alertasSistema.map(alerta => (
                            <div key={alerta.id} className={`alerta-item ${alerta.prioridad}`}>
                                <div className="alerta-info">
                                    <h4>{alerta.tipo}</h4>
                                    <p>{alerta.mensaje}</p>
                                    <span className="alerta-fecha">{alerta.fecha}</span>
                                </div>
                                <div className="alerta-prioridad">
                                    <span className={`prioridad-badge ${alerta.prioridad}`}>
                                        {alerta.prioridad.toUpperCase()}
                                    </span>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Acciones Rápidas */}
            <div className="dashboard-card">
                <div className="card-header">
                    <div className="card-icon">⚡</div>
                    <h3 className="card-title">Acciones Rápidas</h3>
                </div>
                <div className="card-content">
                    <div className="acciones-grid">
                        <button className="btn-primary">Generar Reportes</button>
                        <button className="btn-primary">Backup de Datos</button>
                        <button className="btn-primary">Configurar Sistema</button>
                        <button className="btn-primary">Enviar Notificaciones</button>
                    </div>
                </div>
            </div>

            {/* Modal para Agregar Curso */}
            {showAgregarCurso && (
                <div className="modal-overlay">
                    <div className="modal">
                        <div className="modal-header">
                            <h3>Agregar Nuevo Curso</h3>
                            <button 
                                className="modal-close"
                                onClick={() => setShowAgregarCurso(false)}
                            >
                                ×
                            </button>
                        </div>
                        <form onSubmit={handleAgregarCurso} className="modal-content">
                            <div className="form-group">
                                <label>Nombre del Curso:</label>
                                <input 
                                    type="text" 
                                    value={nuevoCurso.nombre}
                                    onChange={(e) => setNuevoCurso({...nuevoCurso, nombre: e.target.value})}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Descripción (opcional):</label>
                                <textarea 
                                    value={nuevoCurso.descripcion}
                                    onChange={(e) => setNuevoCurso({...nuevoCurso, descripcion: e.target.value})}
                                    placeholder="Descripción del curso..."
                                />
                            </div>
                            <div className="form-group">
                                <label>Docente Asignado (opcional):</label>
                                <input 
                                    type="text" 
                                    value={nuevoCurso.docente}
                                    onChange={(e) => setNuevoCurso({...nuevoCurso, docente: e.target.value})}
                                    placeholder="Se puede asignar después..."
                                />
                            </div>
                            <div className="modal-actions">
                                <button type="button" className="btn-secondary" onClick={() => setShowAgregarCurso(false)}>
                                    Cancelar
                                </button>
                                <button type="submit" className="btn-primary">
                                    Agregar Curso
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            {/* Modal para Gestión de Usuarios */}
            {showGestionUsuarios && (
                <div className="modal-overlay">
                    <div className="modal large">
                        <div className="modal-header">
                            <h3>Gestión Completa de Usuarios</h3>
                            <button 
                                className="modal-close"
                                onClick={() => setShowGestionUsuarios(false)}
                            >
                                ×
                            </button>
                        </div>
                        <div className="modal-content">
                            <div className="usuarios-table">
                                <table>
                                    <thead>
                                        <tr>
                                            <th>Nombre</th>
                                            <th>Correo</th>
                                            <th>Rol</th>
                                            <th>Estado</th>
                                            <th>Fecha Registro</th>
                                            <th>Acciones</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {usuariosRecientes.map(usuario => (
                                            <tr key={usuario.id}>
                                                <td>{usuario.nombre} {usuario.apellido}</td>
                                                <td>{usuario.correo}</td>
                                                <td>
                                                    <select 
                                                        value={usuario.rol}
                                                        onChange={(e) => handleCambiarRol(usuario.id, e.target.value)}
                                                    >
                                                        <option value="Estudiante">Estudiante</option>
                                                        <option value="Docente">Docente</option>
                                                        <option value="Padre">Padre</option>
                                                        <option value="Administrador">Administrador</option>
                                                    </select>
                                                </td>
                                                <td>
                                                    <span className={`status-badge ${usuario.activo ? 'activo' : 'inactivo'}`}>
                                                        {usuario.activo ? 'Activo' : 'Inactivo'}
                                                    </span>
                                                </td>
                                                <td>{usuario.fechaRegistro}</td>
                                                <td>
                                                    <button className="btn-secondary">Editar</button>
                                                    <button className="btn-danger">Eliminar</button>
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Modal para Asignar Docente */}
            {showAsignarDocente && cursoSeleccionado && (
                <div className="modal-overlay">
                    <div className="modal">
                        <div className="modal-header">
                            <h3>Asignar Docente al Curso</h3>
                            <button 
                                className="modal-close"
                                onClick={() => {
                                    setShowAsignarDocente(false);
                                    setCursoSeleccionado(null);
                                }}
                            >
                                ×
                            </button>
                        </div>
                        <div className="modal-content">
                            <div className="curso-info-modal">
                                <h4>Curso: {cursoSeleccionado.nombre}</h4>
                                <p>Selecciona un docente para asignar a este curso:</p>
                            </div>
                            
                            <div className="docentes-list">
                                {docentesDisponibles.length > 0 ? (
                                    docentesDisponibles.map(docente => (
                                        <div key={docente.id} className="docente-item">
                                            <div className="docente-info">
                                                <h5>{docente.nombre} {docente.apellido}</h5>
                                                <p>{docente.correo}</p>
                                                <span className="docente-status">Activo</span>
                                            </div>
                                            <button 
                                                className="btn-primary"
                                                onClick={() => handleConfirmarAsignacion(docente.id)}
                                            >
                                                Asignar
                                            </button>
                                        </div>
                                    ))
                                ) : (
                                    <div className="no-docentes">
                                        <p>No hay docentes disponibles en este momento.</p>
                                        <p>Primero crea usuarios con rol de "Docente".</p>
                                    </div>
                                )}
                            </div>
                            
                            <div className="modal-actions">
                                <button 
                                    type="button" 
                                    className="btn-secondary" 
                                    onClick={() => {
                                        setShowAsignarDocente(false);
                                        setCursoSeleccionado(null);
                                    }}
                                >
                                    Cancelar
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default DashboardAdministrador;
