import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import './Sidebar.css';

const Sidebar = ({ user, isOpen, toggleSidebar }) => {
    const location = useLocation();
    const navigate = useNavigate();
    const { logout } = useAuth();

    const handleLogout = () => {
        logout();
        navigate('/');
    };

    // Funciones seg√∫n el rol
    const getMenuItems = () => {
        if (!user) return [];

        switch (user.rol) {
            case 'Administrador':
                return [
                    { icon: 'üìä', label: 'Dashboard', path: '/dashboard', section: 'Inicio' },
                    { icon: 'üë•', label: 'Gesti√≥n de Usuarios', path: '/dashboard', action: 'usuarios', section: 'Gesti√≥n' },
                    { icon: 'üìö', label: 'Gesti√≥n de Cursos', path: '/dashboard', action: 'cursos', section: 'Gesti√≥n' },
                    { icon: 'üè´', label: 'Gesti√≥n de Salones', path: '/dashboard', action: 'salones', section: 'Gesti√≥n' },
                    { icon: 'üë®‚Äçüë©‚Äçüëß‚Äçüë¶', label: 'Crear Padre e Hijo', path: '/dashboard', action: 'crear-padre-hijo', section: 'Gesti√≥n' },
                    { icon: 'üìß', label: 'Mensajer√≠a', path: '/mensajeria', section: 'Comunicaci√≥n' },
                ];
            case 'Docente':
                return [
                    { icon: 'üìä', label: 'Dashboard', path: '/dashboard', section: 'Inicio' },
                    { icon: 'üìö', label: 'Mis Cursos', path: '/dashboard', action: 'cursos', section: 'Acad√©mico' },
                    { icon: 'üè´', label: 'Mis Salones', path: '/dashboard', action: 'salones', section: 'Acad√©mico' },
                    { icon: 'üìù', label: 'Tareas', path: '/dashboard', action: 'tareas', section: 'Acad√©mico' },
                    { icon: 'üìÖ', label: 'Horarios', path: '/dashboard', action: 'horarios', section: 'Acad√©mico' },
                    { icon: 'üìß', label: 'Mensajer√≠a', path: '/mensajeria', section: 'Comunicaci√≥n' },
                ];
            case 'Estudiante':
                return [
                    { icon: 'üìä', label: 'Dashboard', path: '/dashboard', section: 'Inicio' },
                    { icon: 'üìö', label: 'Mis Cursos', path: '/dashboard', action: 'cursos', section: 'Acad√©mico' },
                    { icon: 'üìù', label: 'Tareas', path: '/tareas', section: 'Acad√©mico' },
                    { icon: 'üìä', label: 'Calificaciones', path: '/calificaciones', section: 'Acad√©mico' },
                    { icon: 'üìß', label: 'Mensajer√≠a', path: '/mensajeria', section: 'Comunicaci√≥n' },
                ];
            case 'Padre':
                return [
                    { icon: 'üìä', label: 'Dashboard', path: '/dashboard', section: 'Inicio' },
                    { icon: 'üë®‚Äçüë©‚Äçüëß‚Äçüë¶', label: 'Mis Hijos', path: '/dashboard', action: 'hijos', section: 'Familiar' },
                    { icon: 'üìö', label: 'Cursos de Hijos', path: '/dashboard', action: 'cursos-hijos', section: 'Familiar' },
                    { icon: 'üìä', label: 'Calificaciones', path: '/dashboard', action: 'calificaciones-hijos', section: 'Familiar' },
                    { icon: 'üìß', label: 'Mensajer√≠a', path: '/mensajeria', section: 'Comunicaci√≥n' },
                ];
            default:
                return [];
        }
    };

    const menuItems = getMenuItems();
    const groupedItems = menuItems.reduce((acc, item) => {
        if (!acc[item.section]) {
            acc[item.section] = [];
        }
        acc[item.section].push(item);
        return acc;
    }, {});

    const handleItemClick = (item) => {
        if (item.action) {
            // Si tiene una acci√≥n, emitir evento personalizado
            window.dispatchEvent(new CustomEvent('dashboard-action', { detail: { action: item.action } }));
        }
        // Cerrar sidebar en m√≥vil despu√©s de hacer clic
        if (window.innerWidth < 768) {
            toggleSidebar();
        }
    };

    return (
        <>
            {/* Overlay para m√≥vil */}
            {isOpen && <div className="sidebar-overlay" onClick={toggleSidebar} />}
            
            {/* Sidebar */}
            <div className={`sidebar ${isOpen ? 'open' : 'closed'}`}>
                <div className="sidebar-header">
                    <h3>Men√∫</h3>
                    <button className="sidebar-toggle" onClick={toggleSidebar}>
                        {isOpen ? '‚Üê' : '‚Üí'}
                    </button>
                </div>

                {/* Informaci√≥n del usuario */}
                {user && isOpen && (
                    <div className="sidebar-user-info">
                        <div className="sidebar-user-avatar">
                            {user.rol === 'Administrador' && 'üë®‚Äçüíº'}
                            {user.rol === 'Docente' && 'üë®‚Äçüè´'}
                            {user.rol === 'Estudiante' && 'üë®‚Äçüéì'}
                            {user.rol === 'Padre' && 'üë®‚Äçüë©‚Äçüëß‚Äçüë¶'}
                        </div>
                        <div className="sidebar-user-details">
                            <div className="sidebar-user-name">{user.nombre} {user.apellido}</div>
                            <div className="sidebar-user-role">{user.rol}</div>
                            {user.correo && (
                                <div className="sidebar-user-email">{user.correo}</div>
                            )}
                        </div>
                    </div>
                )}

                {user && !isOpen && (
                    <div className="sidebar-user-info-collapsed">
                        <div className="sidebar-user-avatar">
                            {user.rol === 'Administrador' && 'üë®‚Äçüíº'}
                            {user.rol === 'Docente' && 'üë®‚Äçüè´'}
                            {user.rol === 'Estudiante' && 'üë®‚Äçüéì'}
                            {user.rol === 'Padre' && 'üë®‚Äçüë©‚Äçüëß‚Äçüë¶'}
                        </div>
                    </div>
                )}
                
                <nav className="sidebar-nav">
                    {Object.entries(groupedItems).map(([section, items]) => (
                        <div key={section} className="sidebar-section">
                            {isOpen && <h4 className="sidebar-section-title">{section}</h4>}
                            <ul className="sidebar-menu">
                                {items.map((item, index) => {
                                    const isActive = location.pathname === item.path && !item.action;
                                    return (
                                        <li key={index} className={`sidebar-item ${isActive ? 'active' : ''}`}>
                                            {item.path ? (
                                                <Link 
                                                    to={item.path} 
                                                    className="sidebar-link"
                                                    onClick={() => handleItemClick(item)}
                                                >
                                                    <span className="sidebar-icon">{item.icon}</span>
                                                    {isOpen && <span className="sidebar-label">{item.label}</span>}
                                                </Link>
                                            ) : (
                                                <button 
                                                    className="sidebar-link"
                                                    onClick={() => handleItemClick(item)}
                                                >
                                                    <span className="sidebar-icon">{item.icon}</span>
                                                    {isOpen && <span className="sidebar-label">{item.label}</span>}
                                                </button>
                                            )}
                                        </li>
                                    );
                                })}
                            </ul>
                        </div>
                    ))}
                </nav>
            </div>
        </>
    );
};

export default Sidebar;
