                                <div style={{ marginTop: '10px', display: 'flex', gap: '10px', justifyContent: 'space-between', alignItems: 'center' }}>
                                    <span className={`status-badge ${salon.activo ? 'activo' : 'inactivo'}`}>
                                        {salon.activo ? 'Activo' : 'Inactivo'}
