                                            onClick={() => {
                                                setNuevaMatricula({
                                                    estudiante: matricula.id_estudiante,
                                                    curso: matricula.id_curso
                                                });
                                                setShowMatricularEstudiante(true);
                                            }}
                                        >
                                            Editar
                                        </button>
                                    </div>
                                </div>
