import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import './Login.css';

const Login = () => {
    const { login } = useAuth();
    const navigate = useNavigate();
    
    // Estados para manejar los datos del formulario
    const [formData, setFormData] = useState({
        correo: '',
        contrasena: ''
    });

    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');
    const [showPassword, setShowPassword] = useState(false);

    // Funci√≥n para manejar cambios en los inputs
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
        // Limpiar mensajes de error al escribir
        if (error) setError('');
        if (message) setMessage('');
    };

    // Funci√≥n para validar el formulario
    const validarFormulario = () => {
        const { correo, contrasena } = formData;

        if (!correo.trim() || !contrasena) {
            setError('Todos los campos son obligatorios');
            return false;
        }

        // Validaci√≥n b√°sica de email
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(correo)) {
            setError('Por favor ingrese un correo electr√≥nico v√°lido');
            return false;
        }

        return true;
    };

    // Funci√≥n para manejar el env√≠o del formulario
    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!validarFormulario()) {
            return;
        }

        setLoading(true);
        setError('');
        setMessage('');

        try {
            const result = await login(formData.correo, formData.contrasena);
            
            if (result.success) {
                setMessage('¬°Inicio de sesi√≥n exitoso! Redirigiendo...');
                setTimeout(() => {
                    navigate('/dashboard');
                }, 1500);
            } else {
                setError(result.message || 'Error al iniciar sesi√≥n');
            }

        } catch (error) {
            console.error('Error en el login:', error);
            setError('Error al iniciar sesi√≥n. Por favor, intente nuevamente.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h2 className="login-title">Iniciar Sesi√≥n</h2>
                <p className="login-subtitle">Accede a tu cuenta</p>

                <form onSubmit={handleSubmit} className="login-form">
                    <div className="form-group">
                        <label htmlFor="correo">Correo Electr√≥nico *</label>
                        <input
                            type="email"
                            id="correo"
                            name="correo"
                            value={formData.correo}
                            onChange={handleChange}
                            className="form-input"
                            placeholder="ejemplo@correo.com"
                            disabled={loading}
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="contrasena">Contrase√±a *</label>
                        <div className="password-input-wrapper">
                            <input
                                type={showPassword ? "text" : "password"}
                                id="contrasena"
                                name="contrasena"
                                value={formData.contrasena}
                                onChange={handleChange}
                                className="form-input"
                                placeholder="Ingrese su contrase√±a"
                                disabled={loading}
                            />
                            <button
                                type="button"
                                className={`password-toggle-btn ${showPassword ? 'show' : 'hide'}`}
                                onClick={() => setShowPassword(!showPassword)}
                                disabled={loading}
                                aria-label={showPassword ? "Ocultar contrase√±a" : "Mostrar contrase√±a"}
                                tabIndex={0}
                            >
                                <span className="password-icon">
                                    {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                                </span>
                            </button>
                        </div>
                    </div>

                    {/* Mostrar mensajes de √©xito o error */}
                    {message && (
                        <div className="message info">
                            {message}
                        </div>
                    )}

                    {error && (
                        <div className="message error">
                            {error}
                        </div>
                    )}

                    <button
                        type="submit"
                        className="submit-button"
                        disabled={loading}
                    >
                        {loading ? 'Iniciando sesi√≥n...' : 'Iniciar Sesi√≥n'}
                    </button>
                </form>

                {/* Informaci√≥n de prueba */}
                <div className="demo-info">
                    <h4>üß™ Cuentas de Prueba:</h4>
                    <p><strong>Estudiante:</strong> estudiante@test.com / password123</p>
                    <p><strong>Docente:</strong> docente@test.com / password123</p>
                    <p><strong>Padre:</strong> padre@test.com / password123</p>
                </div>
            </div>
        </div>
    );
};

export default Login;
