import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth debe ser usado dentro de un AuthProvider');
    }
    return context;
};

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Verificar si hay un usuario guardado en localStorage
        const savedUser = localStorage.getItem('user');
        if (savedUser) {
            setUser(JSON.parse(savedUser));
        }
        setLoading(false);
    }, []);

    const login = async (correo, contrasena) => {
        try {
            // Simular autenticación - en una implementación real harías una llamada al backend
            const response = await fetch('http://localhost:5000/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ correo, contrasena }),
            });

            if (response.ok) {
                const userData = await response.json();
                setUser(userData.user);
                localStorage.setItem('user', JSON.stringify(userData.user));
                localStorage.setItem('token', userData.token);
                return { success: true };
            } else {
                const error = await response.json();
                return { success: false, message: error.message };
            }
        } catch (error) {
            // Simular login para desarrollo - remover en producción
            const mockUsers = {
                'estudiante@test.com': { 
                    id: 1, 
                    nombre: 'Juan', 
                    apellido: 'Pérez', 
                    correo: 'estudiante@test.com', 
                    rol: 'Estudiante' 
                },
                'docente@test.com': { 
                    id: 2, 
                    nombre: 'María', 
                    apellido: 'González', 
                    correo: 'docente@test.com', 
                    rol: 'Docente' 
                },
                'padre@test.com': { 
                    id: 3, 
                    nombre: 'Carlos', 
                    apellido: 'Rodríguez', 
                    correo: 'padre@test.com', 
                    rol: 'Padre' 
                },
                'admin@test.com': { 
                    id: 4, 
                    nombre: 'Ana', 
                    apellido: 'Administradora', 
                    correo: 'admin@test.com', 
                    rol: 'Administrador' 
                }
            };

            if (mockUsers[correo] && contrasena === 'password123') {
                const userData = mockUsers[correo];
                setUser(userData);
                localStorage.setItem('user', JSON.stringify(userData));
                localStorage.setItem('token', 'mock-token');
                return { success: true };
            } else {
                return { success: false, message: 'Credenciales incorrectas' };
            }
        }
    };

    const logout = () => {
        setUser(null);
        localStorage.removeItem('user');
        localStorage.removeItem('token');
    };

    const value = {
        user,
        login,
        logout,
        loading,
        isAuthenticated: !!user
    };

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
};
