import React, { useState } from 'react';
import './Login.css';

const Login = () => {
    // Estados para manejar los datos del formulario
    const [formData, setFormData] = useState({
        correo: '',
        contrasena: ''
    });

    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');

    // Función para manejar cambios en los inputs
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
        // Limpiar mensajes de error al escribir
        if (error) setError('');
        if (message) setMessage('');
    };

    // Función para validar el formulario
    const validarFormulario = () => {
        const { correo, contrasena } = formData;

        if (!correo.trim() || !contrasena) {
            setError('Todos los campos son obligatorios');
            return false;
        }

        // Validación básica de email
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(correo)) {
            setError('Por favor ingrese un correo electrónico válido');
            return false;
        }

        return true;
    };

    // Función para manejar el envío del formulario
    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!validarFormulario()) {
            return;
        }

        setLoading(true);
        setError('');
        setMessage('');

        try {
            // Por ahora simularemos el login ya que no tenemos endpoint implementado
            // En una implementación real, aquí harías la llamada al backend
            setMessage('Funcionalidad de login en desarrollo. Por favor, regístrate primero.');

        } catch (error) {
            console.error('Error en el login:', error);
            setError('Error al iniciar sesión. Por favor, intente nuevamente.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h2 className="login-title">Iniciar Sesión</h2>
                <p className="login-subtitle">Accede a tu cuenta</p>

                <form onSubmit={handleSubmit} className="login-form">
                    <div className="form-group">
                        <label htmlFor="correo">Correo Electrónico *</label>
                        <input
                            type="email"
                            id="correo"
                            name="correo"
                            value={formData.correo}
                            onChange={handleChange}
                            className="form-input"
                            placeholder="ejemplo@correo.com"
                            disabled={loading}
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="contrasena">Contraseña *</label>
                        <input
                            type="password"
                            id="contrasena"
                            name="contrasena"
                            value={formData.contrasena}
                            onChange={handleChange}
                            className="form-input"
                            placeholder="Ingrese su contraseña"
                            disabled={loading}
                        />
                    </div>

                    {/* Mostrar mensajes de éxito o error */}
                    {message && (
                        <div className="message info">
                            {message}
                        </div>
                    )}

                    {error && (
                        <div className="message error">
                            {error}
                        </div>
                    )}

                    <button
                        type="submit"
                        className="submit-button"
                        disabled={loading}
                    >
                        {loading ? 'Iniciando sesión...' : 'Iniciar Sesión'}
                    </button>
                </form>

                <div className="register-link">
                    <p>¿No tienes una cuenta? <a href="/registro">Regístrate aquí</a></p>
                </div>
            </div>
        </div>
    );
};

export default Login;
