            setLoading(true);
            setError(null);

            try {
                // Cargar estadísticas, usuarios, matrículas y cursos en paralelo
                await Promise.all([cargarEstadisticas(), cargarUsuarios(), cargarMatriculas(), cargarCursos()]);
            } catch (error) {
                console.error('Error general al cargar datos:', error);
                setError('Error al cargar los datos del sistema');
            } finally {
                setLoading(false);
            }
        };

        cargarDatos();
