                    <p>{error}</p>
                    <div className="error-actions">
                        <button 
                            className="btn-primary" 
                            onClick={() => {
                                setError(null);
                                setLoading(true);
                                cargarEstadisticas();
                                cargarUsuarios();
                                setLoading(false);
                            }}
                        >
                            Reintentar
                        </button>
                        <button 
                            className="btn-secondary" 
                            onClick={() => window.location.reload()}
                        >
                            Recargar Página
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="dashboard-container">
            {/* Header del Dashboard */}
            <div className="dashboard-header">
                <div>
                    <h1 className="dashboard-title">Dashboard Administrador</h1>
                    <p className="dashboard-subtitle">Panel de control administrativo</p>
                </div>
                <div className="user-info">
                    <p className="user-name">{user.nombre} {user.apellido}</p>
                    <p className="user-role">👑 Administrador</p>
                </div>
            </div>

            {/* Estadísticas Generales */}
            <div className="dashboard-grid">
                <div className="dashboard-card">
                    <div className="card-header">
                        <div className="card-icon">👥</div>
                        <h3 className="card-title">Total Usuarios</h3>
                    </div>
                    <div className="card-content">
                        <p><strong>{estadisticas?.usuarios?.total || 0}</strong> usuarios registrados</p>
                        <p>Estudiantes: {estadisticas?.usuarios?.estudiantes || 0}</p>
                        <p>Docentes: {estadisticas?.usuarios?.docentes || 0}</p>
                        <p>Padres: {estadisticas?.usuarios?.padres || 0}</p>
                        <p>Activos: {estadisticas?.usuarios?.activos || 0}</p>
