import React, { useState, useEffect } from 'react';

const DashboardEstudiante = ({ user }) => {
    const [mensajes, setMensajes] = useState([]);
    const [showMensajeModal, setShowMensajeModal] = useState(false);
    const [nuevoMensaje, setNuevoMensaje] = useState({
        destinatario: '',
        asunto: '',
        contenido: ''
    });

    // Cargar mensajes del estudiante
    useEffect(() => {
        const cargarMensajes = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/mensajes/usuario/${user.id}?tipo=recibidos`);
                if (response.ok) {
                    const data = await response.json();
                    setMensajes(data.data.slice(0, 3)); // Solo los √∫ltimos 3
                }
            } catch (error) {
                console.error('Error al cargar mensajes:', error);
            }
        };

        cargarMensajes();
    }, [user.id]);

    // Enviar mensaje
    const enviarMensaje = async (e) => {
        e.preventDefault();
        
        try {
            const response = await fetch('http://localhost:5000/api/mensajes', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    id_remitente: user.id,
                    id_destinatario: parseInt(nuevoMensaje.destinatario),
                    asunto: nuevoMensaje.asunto,
                    contenido: nuevoMensaje.contenido
                }),
            });

            if (response.ok) {
                alert('Mensaje enviado exitosamente');
                setNuevoMensaje({ destinatario: '', asunto: '', contenido: '' });
                setShowMensajeModal(false);
            } else {
                const error = await response.json();
                alert(`Error al enviar mensaje: ${error.message}`);
            }
        } catch (error) {
            console.error('Error al enviar mensaje:', error);
            alert('Error de conexi√≥n al enviar el mensaje');
        }
    };

    // Datos de ejemplo para el estudiante
    const tareasPendientes = [
        { id: 1, titulo: 'Tarea de Matem√°ticas', materia: 'Matem√°ticas', fechaLimite: '2024-01-15', estado: 'Pendiente' },
        { id: 2, titulo: 'Ensayo de Literatura', materia: 'Literatura', fechaLimite: '2024-01-18', estado: 'Pendiente' },
        { id: 3, titulo: 'Proyecto de Ciencias', materia: 'Ciencias', fechaLimite: '2024-01-20', estado: 'En Progreso' }
    ];

    const calificacionesRecientes = [
        { id: 1, materia: 'Matem√°ticas', tarea: 'Examen Parcial', calificacion: 85, fecha: '2024-01-10' },
