import React, { useState, useEffect } from 'react';
import './Mensajeria.css';

const Mensajeria = ({ user }) => {
    const [mensajesRecibidos, setMensajesRecibidos] = useState([]);
    const [mensajesEnviados, setMensajesEnviados] = useState([]);
    const [tipoVista, setTipoVista] = useState('recibidos'); // 'recibidos', 'enviados', 'nuevo'
    const [mensajeSeleccionado, setMensajeSeleccionado] = useState(null);
    const [usuariosDisponibles, setUsuariosDisponibles] = useState([]);
    const [buscandoUsuarios, setBuscandoUsuarios] = useState(false);
    const [busquedaUsuario, setBusquedaUsuario] = useState('');
    const [nuevoMensaje, setNuevoMensaje] = useState({
        destinatario: '',
        destinatarioNombre: '',
        asunto: '',
        contenido: ''
    });
    const [estadisticas, setEstadisticas] = useState({
        mensajes_recibidos: 0,
        mensajes_enviados: 0,
        mensajes_no_leidos: 0
    });
    const [loading, setLoading] = useState(true);

    // Cargar mensajes y estad칤sticas
    useEffect(() => {
        cargarMensajes();
        cargarEstadisticas();
    }, [user.id, tipoVista]);

    const cargarMensajes = async () => {
        try {
            setLoading(true);
            if (tipoVista === 'recibidos') {
                const response = await fetch(`http://localhost:5000/api/mensajes/usuario/${user.id}?tipo=recibidos`);
                if (response.ok) {
                    const data = await response.json();
                    setMensajesRecibidos(data.data || []);
                }
            } else if (tipoVista === 'enviados') {
                const response = await fetch(`http://localhost:5000/api/mensajes/usuario/${user.id}?tipo=enviados`);
                if (response.ok) {
                    const data = await response.json();
                    setMensajesEnviados(data.data || []);
                }
            }
        } catch (error) {
            console.error('Error al cargar mensajes:', error);
        } finally {
            setLoading(false);
        }
    };

    const cargarEstadisticas = async () => {
        try {
            const response = await fetch(`http://localhost:5000/api/mensajes/estadisticas/${user.id}`);
            if (response.ok) {
                const data = await response.json();
                setEstadisticas(data.data || estadisticas);
            }
        } catch (error) {
            console.error('Error al cargar estad칤sticas:', error);
        }
    };

    // Buscar usuarios para enviar mensaje
    const buscarUsuarios = async (termino) => {
        if (termino.length < 2) {
            setUsuariosDisponibles([]);
            return;
        }

        try {
            setBuscandoUsuarios(true);
            const response = await fetch(`http://localhost:5000/api/admin/usuarios`);
            if (response.ok) {
                const data = await response.json();
                const usuariosFiltrados = (data.data || []).filter(u => 
                    u.activo && 
                    u.id_usuario !== user.id &&
                    (u.nombre.toLowerCase().includes(termino.toLowerCase()) ||
                     u.apellido.toLowerCase().includes(termino.toLowerCase()) ||
                     u.correo.toLowerCase().includes(termino.toLowerCase()) ||
                     (u.rol && u.rol.toLowerCase().includes(termino.toLowerCase())))
                );
                setUsuariosDisponibles(usuariosFiltrados.slice(0, 10)); // Limitar a 10 resultados
            }
        } catch (error) {
            console.error('Error al buscar usuarios:', error);
        } finally {
            setBuscandoUsuarios(false);
        }
    };

    // Seleccionar destinatario
    const seleccionarDestinatario = (usuario) => {
        setNuevoMensaje({
            ...nuevoMensaje,
            destinatario: usuario.id_usuario,
            destinatarioNombre: `${usuario.nombre} ${usuario.apellido} (${usuario.rol})`
        });
        setUsuariosDisponibles([]);
        setBusquedaUsuario('');
    };

    // Enviar mensaje
    const enviarMensaje = async (e) => {
        e.preventDefault();
        
        if (!nuevoMensaje.destinatario || !nuevoMensaje.contenido) {
            alert('Debe seleccionar un destinatario y escribir un mensaje');
            return;
        }

        try {
            const response = await fetch('http://localhost:5000/api/mensajes', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    id_remitente: user.id,
                    id_destinatario: parseInt(nuevoMensaje.destinatario),
                    asunto: nuevoMensaje.asunto || 'Sin asunto',
                    contenido: nuevoMensaje.contenido
                }),
            });

            if (response.ok) {
                alert('Mensaje enviado exitosamente');
                setNuevoMensaje({
                    destinatario: '',
                    destinatarioNombre: '',
                    asunto: '',
                    contenido: ''
                });
                setTipoVista('enviados');
                await cargarMensajes();
                await cargarEstadisticas();
            } else {
                const error = await response.json();
                alert(`Error al enviar mensaje: ${error.message}`);
            }
        } catch (error) {
            console.error('Error al enviar mensaje:', error);
            alert('Error de conexi칩n al enviar el mensaje');
        }
    };

    // Marcar mensaje como le칤do
    const marcarComoLeido = async (idMensaje) => {
        try {
            const response = await fetch(`http://localhost:5000/api/mensajes/${idMensaje}/leido`, {
                method: 'PUT',
            });

            if (response.ok) {
                await cargarMensajes();
                await cargarEstadisticas();
            }
        } catch (error) {
            console.error('Error al marcar mensaje como le칤do:', error);
        }
    };

    // Ver detalles del mensaje
    const verMensaje = async (mensaje) => {
        setMensajeSeleccionado(mensaje);
        if (!mensaje.leido && tipoVista === 'recibidos') {
            await marcarComoLeido(mensaje.id_mensaje);
        }
    };

    const mensajesActuales = tipoVista === 'recibidos' ? mensajesRecibidos : mensajesEnviados;

    return (
        <div className="mensajeria-container">
            <div className="mensajeria-header">
                <h2>游닎 Sistema de Mensajer칤a</h2>
                <div className="mensajeria-stats">
                    <div className="stat-item">
                        <span className="stat-label">Recibidos:</span>
                        <span className="stat-value">{estadisticas.mensajes_recibidos}</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-label">Enviados:</span>
                        <span className="stat-value">{estadisticas.mensajes_enviados}</span>
                    </div>
                    <div className="stat-item unread">
                        <span className="stat-label">No le칤dos:</span>
                        <span className="stat-value">{estadisticas.mensajes_no_leidos}</span>
                    </div>
                </div>
            </div>

            <div className="mensajeria-content">
                {/* Barra de navegaci칩n */}
                <div className="mensajeria-nav">
                    <button
                        className={`nav-btn ${tipoVista === 'recibidos' ? 'active' : ''}`}
                        onClick={() => {
                            setTipoVista('recibidos');
                            setMensajeSeleccionado(null);
                        }}
                    >
                        游닌 Recibidos {estadisticas.mensajes_no_leidos > 0 && (
                            <span className="badge">{estadisticas.mensajes_no_leidos}</span>
                        )}
                    </button>
                    <button
                        className={`nav-btn ${tipoVista === 'enviados' ? 'active' : ''}`}
                        onClick={() => {
                            setTipoVista('enviados');
                            setMensajeSeleccionado(null);
                        }}
                    >
                        游닋 Enviados
                    </button>
                    <button
                        className={`nav-btn ${tipoVista === 'nuevo' ? 'active' : ''}`}
                        onClick={() => {
                            setTipoVista('nuevo');
                            setMensajeSeleccionado(null);
                        }}
                    >
                        九괦잺 Nuevo Mensaje
                    </button>
                </div>

                {/* Contenido principal */}
                <div className="mensajeria-main">
                    {tipoVista === 'nuevo' ? (
                        /* Formulario para nuevo mensaje */
                        <div className="mensajeria-form">
                            <h3>Escribir Nuevo Mensaje</h3>
                            <form onSubmit={enviarMensaje}>
                                <div className="form-group">
                                    <label>Destinatario: *</label>
                                    <div className="destinatario-search">
                                        <input
                                            type="text"
                                            value={busquedaUsuario}
                                            onChange={(e) => {
                                                setBusquedaUsuario(e.target.value);
                                                buscarUsuarios(e.target.value);
                                            }}
                                            placeholder="Buscar por nombre, apellido, correo o rol..."
                                            className="search-input"
                                        />
                                        {buscandoUsuarios && (
                                            <div className="loading-spinner-small">Buscando...</div>
                                        )}
                                        {usuariosDisponibles.length > 0 && (
                                            <div className="usuarios-dropdown">
                                                {usuariosDisponibles.map(usuario => (
                                                    <div
                                                        key={usuario.id_usuario}
                                                        className="usuario-option"
                                                        onClick={() => seleccionarDestinatario(usuario)}
                                                    >
                                                        <div className="usuario-info">
                                                            <strong>{usuario.nombre} {usuario.apellido}</strong>
                                                            <span className="usuario-rol">{usuario.rol}</span>
                                                        </div>
                                                        <div className="usuario-email">{usuario.correo}</div>
                                                    </div>
                                                ))}
                                            </div>
                                        )}
                                    </div>
                                    {nuevoMensaje.destinatarioNombre && (
                                        <div className="destinatario-selected">
                                            <strong>Destinatario seleccionado:</strong> {nuevoMensaje.destinatarioNombre}
                                            <button
                                                type="button"
                                                className="btn-remove"
                                                onClick={() => {
                                                    setNuevoMensaje({
                                                        ...nuevoMensaje,
                                                        destinatario: '',
                                                        destinatarioNombre: ''
                                                    });
                                                }}
                                            >
                                                칑
                                            </button>
                                        </div>
                                    )}
                                </div>
                                <div className="form-group">
                                    <label>Asunto:</label>
                                    <input
                                        type="text"
                                        value={nuevoMensaje.asunto}
                                        onChange={(e) => setNuevoMensaje({...nuevoMensaje, asunto: e.target.value})}
                                        placeholder="Asunto del mensaje (opcional)"
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Mensaje: *</label>
                                    <textarea
                                        value={nuevoMensaje.contenido}
                                        onChange={(e) => setNuevoMensaje({...nuevoMensaje, contenido: e.target.value})}
                                        placeholder="Escribe tu mensaje aqu칤..."
                                        rows="8"
                                        required
                                    />
                                </div>
                                <div className="form-actions">
                                    <button type="submit" className="btn-primary">
                                        游닋 Enviar Mensaje
                                    </button>
                                    <button
                                        type="button"
                                        className="btn-secondary"
                                        onClick={() => {
                                            setNuevoMensaje({
                                                destinatario: '',
                                                destinatarioNombre: '',
                                                asunto: '',
                                                contenido: ''
                                            });
                                            setBusquedaUsuario('');
                                            setUsuariosDisponibles([]);
                                        }}
                                    >
                                        Limpiar
                                    </button>
                                </div>
                            </form>
                        </div>
                    ) : (
                        /* Lista de mensajes y vista de detalle */
                        <div className="mensajeria-list-container">
                            <div className="mensajes-list">
                                <h3>{tipoVista === 'recibidos' ? 'Mensajes Recibidos' : 'Mensajes Enviados'}</h3>
                                {loading ? (
                                    <div className="loading">Cargando mensajes...</div>
                                ) : mensajesActuales.length > 0 ? (
                                    mensajesActuales.map(mensaje => {
                                        const remitenteNombre = tipoVista === 'recibidos' 
                                            ? `${mensaje.remitente_nombre} ${mensaje.remitente_apellido}`
                                            : `${mensaje.destinatario_nombre} ${mensaje.destinatario_apellido}`;
                                        const remitenteRol = tipoVista === 'recibidos'
                                            ? mensaje.remitente_rol
                                            : mensaje.destinatario_rol;

                                        return (
                                            <div
                                                key={mensaje.id_mensaje}
                                                className={`mensaje-item ${!mensaje.leido && tipoVista === 'recibidos' ? 'unread' : ''} ${mensajeSeleccionado?.id_mensaje === mensaje.id_mensaje ? 'selected' : ''}`}
                                                onClick={() => verMensaje(mensaje)}
                                            >
                                                <div className="mensaje-header">
                                                    <div className="mensaje-sender">
                                                        <strong>{remitenteNombre}</strong>
                                                        <span className="mensaje-rol">{remitenteRol}</span>
                                                    </div>
                                                    <div className="mensaje-meta">
                                                        <span className="mensaje-date">
                                                            {new Date(mensaje.fecha_envio).toLocaleString()}
                                                        </span>
                                                        {!mensaje.leido && tipoVista === 'recibidos' && (
                                                            <span className="badge-unread">Nuevo</span>
                                                        )}
                                                    </div>
                                                </div>
                                                <div className="mensaje-subject">
                                                    {mensaje.asunto || 'Sin asunto'}
                                                </div>
                                                <div className="mensaje-preview">
                                                    {mensaje.contenido.substring(0, 100)}
                                                    {mensaje.contenido.length > 100 && '...'}
                                                </div>
                                            </div>
                                        );
                                    })
                                ) : (
                                    <div className="no-messages">
                                        <p>No hay mensajes {tipoVista === 'recibidos' ? 'recibidos' : 'enviados'}.</p>
                                    </div>
                                )}
                            </div>

                            {/* Vista de detalle del mensaje */}
                            {mensajeSeleccionado && (
                                <div className="mensaje-detail">
                                    <div className="mensaje-detail-header">
                                        <button
                                            className="btn-close"
                                            onClick={() => setMensajeSeleccionado(null)}
                                        >
                                            칑
                                        </button>
                                    </div>
                                    <div className="mensaje-detail-content">
                                        <div className="mensaje-detail-meta">
                                            <div>
                                                <strong>
                                                    {tipoVista === 'recibidos'
                                                        ? `${mensajeSeleccionado.remitente_nombre} ${mensajeSeleccionado.remitente_apellido}`
                                                        : `${mensajeSeleccionado.destinatario_nombre} ${mensajeSeleccionado.destinatario_apellido}`
                                                    }
                                                </strong>
                                                <span className="mensaje-rol">
                                                    ({tipoVista === 'recibidos'
                                                        ? mensajeSeleccionado.remitente_rol
                                                        : mensajeSeleccionado.destinatario_rol
                                                    })
                                                </span>
                                            </div>
                                            <div className="mensaje-detail-date">
                                                {new Date(mensajeSeleccionado.fecha_envio).toLocaleString()}
                                            </div>
                                        </div>
                                        <div className="mensaje-detail-subject">
                                            <strong>Asunto:</strong> {mensajeSeleccionado.asunto || 'Sin asunto'}
                                        </div>
                                        <div className="mensaje-detail-body">
                                            {mensajeSeleccionado.contenido}
                                        </div>
                                        {tipoVista === 'recibidos' && (
                                            <div className="mensaje-detail-actions">
                                                <button
                                                    className="btn-primary"
                                                    onClick={() => {
                                                        setTipoVista('nuevo');
                                                        setNuevoMensaje({
                                                            destinatario: mensajeSeleccionado.remitente_nombre ? '' : '',
                                                            destinatarioNombre: '',
                                                            asunto: `Re: ${mensajeSeleccionado.asunto || ''}`,
                                                            contenido: `\n\n--- Mensaje original ---\n${mensajeSeleccionado.contenido}`
                                                        });
                                                        setMensajeSeleccionado(null);
                                                    }}
                                                >
                                                    游닎 Responder
                                                </button>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            )}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Mensajeria;
