                body: JSON.stringify({
                    nombre_curso: nuevoCurso.nombre,
                    descripcion: nuevoCurso.descripcion || null,
                    id_docente: idDocente
                }),
            });

            if (response.ok) {
                const data = await response.json();
                console.log('Curso creado exitosamente:', data);
